import React, { useEffect, useState } from 'react';
import apiClient from '../services/apiClient';

const EntidadesPage = () => {
  const [entities, setEntities] = useState([]);
  const [entityTypes, setEntityTypes] = useState([]);
  const [newEntity, setNewEntity] = useState({
    name: '',
    entity_type: '',
    contact_info: '',
  });

  useEffect(() => {
    fetchEntities();
    fetchEntityTypes();
  }, []);

  const fetchEntities = async () => {
    const response = await apiClient.get('/entities/');
    setEntities(response.data);
  };

  const fetchEntityTypes = async () => {
    const response = await apiClient.get('/entity-types/');
    setEntityTypes(response.data);
  };

  const handleInputChange = (e) => {
    setNewEntity({ ...newEntity, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await apiClient.post('/entities/', newEntity);
    fetchEntities();
  };

  return (
    <div className="container mt-5">
      <h1>Gestión de Entidades</h1>
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="mb-3">
          <label className="form-label">Nombre</label>
          <input
            type="text"
            className="form-control"
            name="name"
            value={newEntity.name}
            onChange={handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Tipo de Entidad</label>
          <select
            className="form-select"
            name="entity_type"
            value={newEntity.entity_type}
            onChange={handleInputChange}
          >
            <option value="">Seleccionar</option>
            {entityTypes.map((type) => (
              <option key={type.id} value={type.id}>
                {type.name}
              </option>
            ))}
          </select>
        </div>
        <div className="mb-3">
          <label className="form-label">Información de Contacto</label>
          <textarea
            className="form-control"
            name="contact_info"
            value={newEntity.contact_info}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Agregar Entidad
        </button>
      </form>
      <h2>Lista de Entidades</h2>
      <ul className="list-group">
        {entities.map((entity) => (
          <li key={entity.id} className="list-group-item">
            {entity.name} ({entity.entity_type_name})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EntidadesPage;
